import React, { Component } from "react";
import ReactDOM from "react-dom";
import HomeScreen from "./home_components/HomeScreen";
import Services from "./home_components/Services";
import EMR from "./home_components/EMR";
import PracticeMan from "./home_components/PracticeMan";
import RCMAnalytics from "./home_components/RCMAnalytics";
import PatientApp from "./home_components/PatientApp";
import LetsTalk from "./home_components/LetsTalk";
import ContactUs from "./ContactUsHome";

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      contactUs : this.props.contactUs,
      btnHandler : this.props.btnHandler
    }
  }

  // show and hide contact us card
  toggleContactUs() {
    if (this.state.contactUs === "") {
      this.setState({ contactUs: "active", page: "hide" });
    } else {
      this.setState({ contactUs: "", page: "" });
    }
  }

  render() {
    return (
      <React.Fragment>
        <ContactUs
          classToAdd={this.state.contactUs}
          btnHandler={this.state.toggleContactUs}
        />
        <HomeScreen btnHandler={this.state.toggleContactUs} classToAdd={this.state.homePage}/>
        <Services />
        <EMR />
        <PracticeMan />
        <RCMAnalytics />
        <PatientApp />
        <LetsTalk btnHandler={this.state.toggleContactUs} />
      </React.Fragment>
    );
  }
}

export default Home;
