import react, { Component } from "react";
import "./App.css";
import "./App_Responsive.css";
import "./inter-cufonfonts-webfont/style.css";
import "./syne-cufonfonts-webfont/style.css";
import Nav from "./components/Nav";
import Home from "./components/Home";
import About from "./components/About";
import Terms from "./components/Terms";
import Footer from "./components/Footer";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import ScrollToTop from "./components/ScrollToTop";

class App extends Component {

  constructor(props) {
    super(props)

    this.state = {
      contactUs : "",
      page : ""
    };

    this.toggleContactUs = this.toggleContactUs.bind(this);
  }

  // show and hide contact us card
  toggleContactUs() {
    if (this.state.contactUs == "") {
      this.setState({ contactUs: "active", page: "hide" });
    } else {
      this.setState({ contactUs: "", page: "" });
    }
  }

  render() {
    return (
      <Router>
        <div className="App">
          <Nav />
          <ScrollToTop>
            <Switch>
              <Route path="/" exact component={() => (<Home classToAdd={this.state.contactUs} btnHandler={this.toggleContactUs}/>)} />
              <Route path="/about" component={() => (<About classToAdd={this.state.contactUs} btnHandler={this.toggleContactUs}/>)} />
              <Route path="/terms" component={() => (<Terms classToAdd={this.state.contactUs} btnHandler={this.toggleContactUs}/>)} />
            </Switch>
          </ScrollToTop>
          <Footer />
        </div>
      </Router>
    );
  }
}

export default App;
