import React, { Component } from "react";
import ExitButton from "../images/exit-button-img.svg";
import EmailIcon from "../images/email-img.svg";
import NameIcon from "../images/name-img.svg";

class ContactUs extends Component {
  constructor(props) {
    super(props);

    this.state = {
      radioBtn: "",
      radioYes: "",
      radioNo: "",
    };

    this.radioHandler = this.radioHandler.bind(this);
  }

  radioHandler(event) {
    const value = event.target.value;

    // form state changes + css class modifer
    if (value == "yes") {
      this.setState({
        radioBtn: value,
        radioYes: "active",
        radioNo: "",
      });
    } else {
      this.setState({
        radioBtn: value,
        radioYes: "active",
        radioNo: "",
      });
    }

  }

  render() {
    return (
      <React.Fragment>
        <div className={"absolute-parent-home " + this.props.classToAdd}>
          <div className="contact-us-frame">
            <div className="contact-us-rec1"></div>
            <div className="contact-us-rec2"></div>
            <div className="contact-us-container">
              <div className={"exit-btn-container"}>
                <img
                  src={ExitButton}
                  className="exit-btn-container__button"
                  onClick={this.props.btnHandler}
                ></img>
              </div>
              <h2> Get in touch </h2>
              <h3>Enter your details below</h3>
              <div className="input-container">
                <img src={NameIcon}></img>
                <input placeholder="First Name" type="text"></input>
              </div>
              <div className="input-container">
                <img src={NameIcon}></img>
                <input placeholder="Position" type="text"></input>
              </div>
              <div className="input-container">
                <img src={EmailIcon}></img>
                <input placeholder="Email Address" type="text"></input>
              </div>
              <h3>Are you contacting us on behalf of an urgent care clinic?</h3>

              <div className="radio-btn-container">
                <input
                  type="radio"
                  id="yes"
                  value="yes"
                  name="radio"
                  onChange={this.radioHandler}
                ></input>
                <label for="yes" className="radio-yes">
                  YES
                </label>
                <input
                  type="radio"
                  id="no"
                  value="no"
                  name="radio"
                  onChange={this.radioHandler}
                ></input>
                <label for="no" className={"radio-no" + this.state.radioNo}>
                  NO
                </label>
              </div>

              <textarea
                type="text"
                className="message"
                placeholder="Message"
              ></textarea>
              <div className="submit-container">
                <button type="submit" className="submit-btn">
                  Send now
                </button>
                <button className="cancel-btn">Cancel</button>
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default ContactUs;
