import React, { Component } from "react";
import ExitButton from "../images/exit-button-img.svg";
import EmailIcon from "../images/email-img.svg";
import NameIcon from "../images/name-img.svg";

class ContactUs extends Component {
  constructor(props) {
    super(props);

    this.state = {
      fullName : "",
      position : "",
      email : "",
      radioBtn: "",
      message: "",
      radioYes: "",
      radioNo: ""
    };

    this.radioHandler = this.radioHandler.bind(this);
    this.nameHandler = this.nameHandler.bind(this)
    this.positionHandler = this.positionHandler.bind(this)
    this.emailHandler = this.emailHandler.bind(this)
    this.messageHandler = this.messageHandler.bind(this)
  }


  nameHandler = (event) => {
    this.setState({
      fullName : event.target.value
    })
  }

  positionHandler = (event) => {
    this.setState({
      position : event.target.value
    })
  }

  emailHandler = (event) => {
    this.setState({
      email : event.target.value
    })
  }

  messageHandler = (event) => {
    this.setState({
      message : event.target.value
    })
  }

  radioHandler = (event) => {
    const value = event.target.value;

    // form state changes + css class modifier
    if (value == "yes") {
      this.setState({
        radioBtn: value,
        radioYes: "active",
        radioNo: "",
      });
    } else {
      this.setState({
        radioBtn: value,
        radioYes: "",
        radioNo: "active",
      });
    }
  }

  submitHandler(event) {
   alert(`${this.state.fullName}, ${this.state.position}, ${this.state.message}, ${this.state.email}, ${this.state.radioBtn}`)
  }

  render() {
    return (
      <React.Fragment>
        <div className={"absolute-parent-home " + this.props.classToAdd}>
          <div className="contact-us-frame">
            <div className="contact-us-rec1"></div>
            <div className="contact-us-rec2"></div>
            <div className="contact-us-container">

              <div className={"exit-btn-container"}>
                <img
                  src={ExitButton}
                  className="exit-btn-container__button"
                  onClick={this.props.btnHandler}
                ></img>
              </div>
              <h2> Get in touch </h2>
              <h3>Enter your details below</h3>
              <div className="input-container">
                <img src={NameIcon}></img>
                <input placeholder="Full Name" type="text" value={this.state.fullName} onChange={this.nameHandler.bind(this)}></input>
              </div>
              <div className="input-container">
                <img src={NameIcon}></img>
                <input placeholder="Position" type="text" value={this.state.position} onChange={this.positionHandler.bind(this)}></input>
              </div>
              <div className="input-container">
                <img src={EmailIcon}></img>
                <input placeholder="Email Address" type="text" value={this.state.email} onChange={this.emailHandler.bind(this)}></input>
              </div>
              <h3>Are you contacting us on behalf of an urgent care clinic?</h3>

              <div className="radio-btn-container">
                <input
                  type="radio"
                  id="yes"
                  value="yes"
                  name="radio"
                  onChange={this.radioHandler.bind(this)}
                ></input>

                <label for="yes" className={"radio-yes " + this.state.radioYes}>
                  YES
                </label>

                <input
                  type="radio"
                  id="no"
                  value="no"
                  name="radio"
                  onChange={this.radioHandler.bind(this)}
                ></input>

                <label for="no" className={"radio-no " + this.state.radioNo}>
                  NO
                </label>

              </div>

              <textarea
                type="text"
                className="message"
                placeholder="Message"
                onChange={this.messageHandler}
              >
              </textarea>

              <div className="submit-container">
                <button type="submit" className="submit-btn" onClick={this.submitHandler.bind(this)}>
                  Send now
                </button>
                <button className="cancel-btn" onClick={this.props.btnHandler}>Cancel</button>
              </div>

            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default ContactUs;
