import react, { Component } from "react";
import "./App.css";
import "./App_Responsive.css";
import "./inter-cufonfonts-webfont/style.css";
import "./syne-cufonfonts-webfont/style.css";
import Home from "./components/Home";
import About from "./components/About";
import Terms from "./components/Terms";
import Nav from "./components/Nav";
import Footer from "./components/Footer";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import ScrollToTop from "./components/ScrollToTop";

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      page : ""
    };

    this.toggleAppDiv = this.toggleAppDiv.bind(this);
  }

  toggleAppDiv() {
    if (this.state.page == "") {
      this.setState({page : "hide"})
    } else {
      this.setState({page : ""})
    }
  }

  render() {
    return (
      <Router>
        <div className="App">
          <Nav />
          <ScrollToTop>
            <Switch>
              <Route path="/" exact component={() => (<Home appDivHandler={this.toggleAppDiv}/>)} />
              <Route path="/about" component={() => (<About/>)} />
              <Route path="/terms" component={() => (<Terms/>)} />
            </Switch>
          </ScrollToTop>
          <Footer />
        </div>
      </Router>
    );
  }
}

export default App;
